import React from "react";
import { Link } from "react-router-dom";
import { deleteEmployee } from "../../actions/EmployeeActions";
import { useDispatch } from "react-redux";

function Employee(props) {
  const dispatch = useDispatch();

  const onDeleteClick = (emp_id) => {
    dispatch(deleteEmployee(emp_id));
  };

  const { employee } = props;
  const dep_id = props.id;
  return (
    <React.Fragment>
  <div className="container bg-white p-4 rounded-lg mb-4 shadow-md">
    <div className="flex items-center justify-between">
      <div className="flex-1">
        <h3 className="text-xl font-semibold mb-2">{employee.name}</h3>
        <p className="text-gray-600">{employee.address}</p>
        <p className="text-gray-600">{employee.email}</p>
        <p className="text-gray-600">{employee.phone}</p>
      </div>
      <div className="lg:block hidden">
        <ul className="space-y-4">
          <li>
            <Link
              to={`/employeeBoard/${dep_id}/${employee.id}`}
              className="text-white hover:text-white focus:outline-none bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-full transition duration-300 ease-in-out transform hover:scale-105 block w-full text-center"
            >
              Employee board
            </Link>
          </li>
          <li>
            <Link
              to={`/updateEmployee/${dep_id}/${employee.id}`}
              className="text-white hover:text-white focus:outline-none bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-full transition duration-300 ease-in-out transform hover:scale-105 block w-full text-center"
            >
              Update employee
            </Link>
          </li>
          <li>
            <button
              className="bg-red-500 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded-full transition duration-300 ease-in-out transform hover:scale-105 block w-full text-center"
              onClick={() => onDeleteClick(employee.id)}
            >
              Delete employee
            </button>
          </li>
        </ul>
      </div>
    </div>
  </div>
</React.Fragment>
  );
}

export default Employee;
