import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import PropTypes from "prop-types";
import { getDepartment, createDepartment } from "../../actions/DepartmentActions";

const UpdateDepartment = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const errors = useSelector((state) => state.errorsReducerContent);
  const department = useSelector((state) => state.departmentReducerContent.department);

  const [state, setState] = useState({
    id: id,
    name: "",
    errors: {},
  });

  useEffect(() => {
    dispatch(getDepartment(id));
  }, [dispatch, id]);

  useEffect(() => {
    setState((prevState) => ({
      ...prevState,
      id: id,
      name: department.name || "",
      errors: errors,
    }));
  }, [department, id, errors]);

  const onChange = (e) => {
    const { name, value } = e.target;
    setState((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const onSubmit = (e) => {
    e.preventDefault();

    const updateProject = {
      id: state.id,
      name: state.name,
    };
    dispatch(createDepartment(updateProject));
  };

  return (
    <div>
      <div className="register">
        <div className="container">
          <div className="grid grid-cols-1 gap-6">
            <div className="m-auto">
              <h5 className="text-4xl text-center">Update Department form</h5>
              <hr />
              <form onSubmit={onSubmit}>
                <div className="mb-6">
                  <input
                    type="text"
                    className="block w-full p-3 border rounded-lg"
                    placeholder="Department id"
                    name="id"
                    value={state.id}
                    onChange={onChange}
                    disabled
                  />
                </div>
                <div className="mb-6">
                  <input
                    type="text"
                    className={`block w-full p-3 border rounded-lg ${
                      errors.name ? "border-red-500" : ""
                    }`}
                    placeholder="Department name"
                    name="name"
                    value={state.name}
                    onChange={onChange}
                  />
                  {errors.name && (
                    <div className="text-red-500">{errors.name}</div>
                  )}
                </div>

                <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full w-full">
                  Update Department
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

UpdateDepartment.propTypes = {
  // Route parameter: Department ID
  id: PropTypes.string.isRequired,
};

export default UpdateDepartment;
