// EmployeeManagementPage.js

import React from 'react';

const EmployeeManagementPage = () => {
  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-500 to-blue-700 text-white">
      {/* Header */}
      <header className="py-4 px-8 flex justify-between items-center bg-blue-900">
        <h1 className="text-3xl font-extrabold tracking-wider">Employee Dashboard</h1>
        <button className="bg-blue-700 px-4 py-2 rounded-md">Log Out</button>
      </header>

      {/* Hero Section */}
      <section className="flex items-center justify-center flex-1 bg-gray-100">
        <div className="text-center">
          <h2 className="text-5xl font-extrabold mb-4">Welcome to Your Employee Dashboard</h2>
          <p className="text-xl mb-8">Efficiently manage your workforce with ease.</p>
          <button className="bg-blue-500 px-6 py-3 text-white font-bold rounded-md hover:bg-blue-700">
            Get Started
          </button>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-16 bg-gray-200">
        <div className="max-w-4xl mx-auto">
          <h2 className="text-3xl font-bold text-center mb-8">Key Features</h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {/* Feature 1 */}
            <div className="bg-white p-8 rounded-md shadow-md">
              <h3 className="text-2xl font-bold mb-4">Employee Directory</h3>
              <p>Effortlessly organize and manage your employee data.</p>
            </div>

            {/* Feature 2 */}
            <div className="bg-white p-8 rounded-md shadow-md">
              <h3 className="text-2xl font-bold mb-4">Task Management</h3>
              <p>Assign and track tasks to enhance productivity.</p>
            </div>

            {/* Feature 3 */}
            <div className="bg-white p-8 rounded-md shadow-md">
              <h3 className="text-2xl font-bold mb-4">Reports & Analytics</h3>
              <p>Gain insights into your workforce with comprehensive analytics.</p>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default EmployeeManagementPage;
