import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { Provider } from "react-redux";
import store from "./store";
import Header from "./components/Layout/Header";
import Departments from "./components/Departments";
import AddDepartment from "./components/departments/AddDepartment";
import UpdateDepartment from "./components/departments/UpdateDepartment";
import DepartmentBoard from "./components/DepartmentBoard";
import AddEmployee from "./components/employees/AddEmployees";
import UpdateEmployee from "./components/employees/UpdateEmployees";
import EmployeeBoard from "./components/EmployeeBoard";
import AddTaskForm from "./components/tasks/AddTaskForm";
import UpdateTask from "./components/tasks/UpdateTask";
import Landing from "./components/Landing";
import Login from "./components/LogIn";
import Register from "./components/Register";

function checkAuthentication() {
  // Replace this with your actual authentication logic
  // For example, check if a user is logged in or has a valid token.
  // Return true for authenticated users, false otherwise.
  return true; // Replace with your authentication logic
}

function App() {
  const isAuthenticated = checkAuthentication();

  return (
    <Provider store={store}>
      <Router>
        <div>
          <Header />
          <Routes>
            <Route path="/" element={<Landing />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <PrivateRoute
              path="/departments"
              element={<Departments />}
              isAuthenticated={isAuthenticated}
            />
            <PrivateRoute
              path="/addDepartment"
              element={<AddDepartment />}
              isAuthenticated={isAuthenticated}
            />
            <PrivateRoute
              path="/updateDepartment/:id"
              element={<UpdateDepartment />}
              isAuthenticated={isAuthenticated}
            />
            <PrivateRoute
              path="/employees/:id"
              element={<DepartmentBoard />}
              isAuthenticated={isAuthenticated}
            />
            <PrivateRoute
              path="/addEmployee/:id"
              element={<AddEmployee />}
              isAuthenticated={isAuthenticated}
            />
            <PrivateRoute
              path="/updateEmployee/:dep_id/:id"
              element={<UpdateEmployee />}
              isAuthenticated={isAuthenticated}
            />
            <PrivateRoute
              path="/employeeBoard/:dep_id/:emp_id"
              element={<EmployeeBoard />}
              isAuthenticated={isAuthenticated}
            />
            <PrivateRoute
              path="/addTask/:dep_id/:emp_id"
              element={<AddTaskForm />}
              isAuthenticated={isAuthenticated}
            />
            <PrivateRoute
              path="/updateTask/:dep_id/:emp_id/:id"
              element={<UpdateTask />}
              isAuthenticated={isAuthenticated}
            />
          </Routes>
        </div>
      </Router>
    </Provider>
  );
}

function PrivateRoute({ element, isAuthenticated }) {
  return isAuthenticated ? (
    element
  ) : (
    <Navigate to="/login" />
  );
}

export default App;
