import React from "react";
import { Link } from "react-router-dom";
import { deleteEmployee } from "../../actions/EmployeeActions";
import { useDispatch } from "react-redux";

function Employee(props) {
  const dispatch = useDispatch();

  const onDeleteClick = (emp_id) => {
    dispatch(deleteEmployee(emp_id));
  };

  const { employee } = props;
  const dep_id = props.id;
  return (
    <React.Fragment>
      <div className="container">
        <div className="bg-gray-200 p-4 rounded-lg mb-4">
          <div className="flex items-center justify-between">
            <div className="flex-1">
              <h3 className="text-xl font-semibold mb-2">{employee.name}</h3>
              <p className="text-gray-600">{employee.address}</p>
              <p className="text-gray-600">{employee.email}</p>
              <p className="text-gray-600">{employee.phone}</p>
            </div>
            <div className="lg:block hidden">
              <ul className="flex space-x-4">
                <li>
                  <Link
                    to={`/employeeBoard/${dep_id}/${employee.id}`}
                    className="text-blue-500 hover:underline"
                  >
                    Employee board
                  </Link>
                </li>
                <li>
                  <Link
                    to={`/updateEmployee/${dep_id}/${employee.id}`}
                    className="text-blue-500 hover:underline"
                  >
                    Update employee
                  </Link>
                </li>
                <li
                  className="text-red-500 cursor-pointer hover:underline"
                  onClick={() => onDeleteClick(employee.id)}
                >
                  Delete employee
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
}

export default Employee;
