import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { Provider } from "react-redux";
import store from "./store";
import Header from "./components/Layout/Header";
import Departments from "./components/Departments";
import AddDepartment from "./components/departments/AddDepartment";
import UpdateDepartment from "./components/departments/UpdateDepartment";
import DepartmentBoard from "./components/DepartmentBoard";
import AddEmployee from "./components/employees/AddEmployees";
import UpdateEmployee from "./components/employees/UpdateEmployees";
import EmployeeBoard from "./components/EmployeeBoard";
import AddTaskForm from "./components/tasks/AddTaskForm";
import UpdateTask from "./components/tasks/UpdateTask";
import Landing from "./components/Landing";
import Login from "./components/LogIn";
import Register from "./components/Register";

// Define a fake authentication check (replace with your actual logic)
const isAuthenticated = true; // Change to your actual authentication check

// Custom PrivateRoute component
function PrivateRoute({ element, ...rest }) {
  return (
    <Route
      {...rest}
      element={isAuthenticated ? (
        element
      ) : (
        <Navigate to="/login" />
      )}
    />
  );
}

function App() {
  return (
    <Provider store={store}>
      <Router>
        <Routes>
          <Route path="/" element={<Landing />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          <Route
            path="/*"
            element={
              <>
                <Header />
                <Routes>
                  <PrivateRoute path="/departments" element={<Departments />} />
                  <PrivateRoute
                    exact
                    path="/addDepartment"
                    element={<AddDepartment />}
                  />
                  <PrivateRoute
                    exact
                    path="/updateDepartment/:id"
                    element={<UpdateDepartment />}
                  />
                  <PrivateRoute
                    exact
                    path="/employees/:id"
                    element={<DepartmentBoard />}
                  />
                  <PrivateRoute
                    exact
                    path="/addEmployee/:id"
                    element={<AddEmployee />}
                  />
                  <PrivateRoute
                    exact
                    path="/updateEmployee/:dep_id/:id"
                    element={<UpdateEmployee />}
                  />
                  <PrivateRoute
                    exact
                    path="/employeeBoard/:dep_id/:emp_id"
                    element={<EmployeeBoard />}
                  />
                  <PrivateRoute
                    exact
                    path="/addTask/:dep_id/:emp_id"
                    element={<AddTaskForm />}
                  />
                  <PrivateRoute
                    exact
                    path="/updateTask/:dep_id/:emp_id/:id"
                    element={<UpdateTask />}
                  />
                </Routes>
              </>
            }
          />
        </Routes>
      </Router>
    </Provider>
  );
}

export default App;
