import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import { updateTask, getTask } from "../../actions/TaskActions";

const UpdateTask = () => {
  const { dep_id, emp_id, id } = useParams();
  const dispatch = useDispatch();
  const errors = useSelector((state) => state.errorsReducerContent);
  const task = useSelector((state) => state.taskReducerContent.task);
  
  const [state, setState] = useState({
    summary: "",
    acceptanceCriteria: "",
    status: "INPUT QUEUE",
    priority: 3,
    errors: {},
  });

  useEffect(() => {
    dispatch(getTask(dep_id, emp_id, id));
  }, [dispatch, dep_id, emp_id, id]);

  useEffect(() => {
    setState((prevState) => ({
      ...prevState,
      id: id,
      summary: task.summary || "",
      acceptanceCriteria: task.acceptanceCriteria || "",
      priority: task.priority || "",
      status: task.status || "",
      errors: errors,
    }));
  }, [task, id, errors]);

  const onChange = (e) => {
    setState((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const onSubmit = (e) => {
    e.preventDefault();
    const updatedTask = {
      summary: state.summary,
      status: state.status,
      acceptanceCriteria: state.acceptanceCriteria,
      priority: state.priority,
    };
    dispatch(updateTask(updatedTask, dep_id, emp_id, id)); // Use updateTask here
  };

  return (
    <div className="container">
    <div className="add-PBI">
      <div className="container">
        <div className="row">
          <div className="col-md-8 m-auto">
            <Link
              to={`/employeeBoard/${dep_id}/${emp_id}`}
              className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg block w-1/3 mx-auto my-4"
            >
              Back to Employee Board
            </Link>
            <h4 className="text-2xl text-center mt-6">Update Task</h4>
            <div className="task-list mt-6">
              {tasks.map(task => (
                <div className="task-item" key={task.id}>
                  <div className="task-header">
                    <span>ID: {task.id} -- Priority: {task.priority}</span>
                  </div>
                  <div className="task-content">
                    <h3>{task.summary}</h3>
                    <p>{task.acceptanceCriteria}</p>
                  </div>
                  <div className="task-actions">
                    <button className="btn-view-update">View / Update</button>
                    <button className="btn-delete">Delete</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
  
  );
};

export default UpdateTask;
