import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Login2 = ({ setIsAuthenticated }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create an object with the login data
    const loginData = {
      email,
      password,
    };

    try {
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      if (response.status === 200) {
        // Login was successful
        localStorage.setItem('userLoggedIn', 'true');
        // Set userLoggedIn to true
        setIsAuthenticated(true); // Set isAuthenticated to true
        console.log('Login successful');
        navigate('/restaurants');
      } else if (response.status === 401) {
        // Authentication failed
        const errorData = await response.json();
        console.error('Login failed:', errorData.error);
      } else {
        // Handle other error cases
        console.error('Unexpected error:', response.statusText);
      }
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-white via-yellow-200 to-orange-400">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-lg text-center">
        {/* Background Image Overlay */}
        <div className="absolute inset-0 bg-gradient-to-r from-white via-yellow-200 to-orange-400 opacity-70 rounded-lg"></div>
        <h2 className="text-3xl font-extrabold text-gray-800 mb-6 relative z-10">
          <span className="text-red-600">
            <i className="fas fa-utensils mr-2"></i>
          </span>
          Log in to your Restaurant Account
        </h2>
        <form className="space-y-6 relative z-10" onSubmit={handleSubmit}>
          <div className="rounded-md shadow-sm -space-y-4">
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                <span className="text-yellow-400">
                  <i className="fas fa-envelope mr-2"></i>
                </span>
                Email
              </label>
              <input
                id="email"
                name="email"
                type="email"
                autoComplete="email"
                required
                className="appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-yellow-500 focus:border-yellow-500 focus:z-10 sm:text-sm"
                placeholder="Email address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                <span className="text-yellow-400">
                  <i className="fas fa-lock mr-2"></i>
                </span>
                Password
              </label>
              <input
                id="password"
                name="password"
                type="password"
                autoComplete="current-password"
                required
                className="appearance-none rounded-md relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-yellow-500 focus:border-yellow-500 focus:z-10 sm:text-sm"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
          </div>

          <div>
            <button
              type="submit"
              className="w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
            >
              Log in
            </button>
          </div>
        </form>
        <div className="text-black-700 text-sm mt-4">
          Don't have an account?{' '}
          <Link to="/register" className="text-yellow-800">
            Register here
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login2;
