import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createEmployee } from "../../actions/EmployeeActions";
import { useParams } from "react-router-dom";

const AddEmployee = () => {
  const dispatch = useDispatch();
  const errors = useSelector((state) => state.errorsReducerContent);

  const { id } = useParams();

  const [employee, setEmployee] = useState({
    name: "",
    address: "",
    email: "",
    phone: "",
  });

  const onChange = (e) => {
    const { name, value } = e.target;
    setEmployee((prevEmployee) => ({
      ...prevEmployee,
      [name]: value,
    }));
  };

  const onSubmit = (e) => {
    e.preventDefault();
    const newEmployee = {
      name: employee.name,
      address: employee.address,
      email: employee.email,
      phone: employee.phone,
    };
    dispatch(createEmployee(newEmployee, id));
  };

  return (
    <div>
      <div className="employee">
        <div className="container">
          <div className="grid grid-cols-1 gap-6">
            <div className="m-auto">
              <h5 className="text-4xl text-center">Create employee form</h5>
              <hr />
              <form onSubmit={onSubmit}>
                <div className="mb-6">
                  <input
                    type="text"
                    className={`w-full p-3 border rounded-lg ${
                      errors.name ? "border-red-500" : ""
                    }`}
                    placeholder="Employee Name"
                    name="name"
                    value={employee.name}
                    onChange={onChange}
                  />
                  {errors.name && (
                    <div className="text-red-500">{errors.name}</div>
                  )}
                </div>
                <div className="mb-6">
                  <input
                    type="text"
                    className={`w-full p-3 border rounded-lg ${
                      errors.address ? "border-red-500" : ""
                    }`}
                    placeholder="Employee address"
                    name="address"
                    value={employee.address}
                    onChange={onChange}
                  />
                  {errors.address && (
                    <div className="text-red-500">{errors.address}</div>
                  )}
                </div>
                <div className="mb-6">
                  <input
                    type="text"
                    className={`w-full p-3 border rounded-lg ${
                      errors.email ? "border-red-500" : ""
                    }`}
                    placeholder="Employee email"
                    name="email"
                    value={employee.email}
                    onChange={onChange}
                  />
                  {errors.email && (
                    <div className="text-red-500">{errors.email}</div>
                  )}
                </div>
                <div className="mb-6">
                  <input
                    type="text"
                    className={`w-full p-3 border rounded-lg ${
                      errors.phone ? "border-red-500" : ""
                    }`}
                    placeholder="Phone number"
                    name="phone"
                    value={employee.phone}
                    onChange={onChange}
                  />
                  {errors.phone && (
                    <div className="text-red-500">{errors.phone}</div>
                  )}
                </div>

                <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full w-full">
                  Create Employee
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddEmployee;
