import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { getEmployee, createEmployee } from "../../actions/EmployeeActions";

const UpdateEmployee = () => {
  const { dep_id } = useParams();
  const { id } = useParams();
  const dispatch = useDispatch();
  const errors = useSelector((state) => state.errorsReducerContent);
  const employee = useSelector((state) => state.employeeReducerContent.employee);

  const [state, setState] = useState({
    id: "",
    name: "",
    address: "",
    email: "",
    phone: "",
    departmentBacklog: "",
    errors: {},
  });

  useEffect(() => {
    dispatch(getEmployee(dep_id, id));
  }, [dispatch, dep_id, id]);

  useEffect(() => {
    setState((prevState) => ({
      ...prevState,
      id: id,
      name: employee.name || "",
      address: employee.address || "",
      email: employee.email || "",
      phone: employee.phone || "",
      errors: errors,
    }));
  }, [employee, id, errors]);

  const onChange = (e) => {
    setState((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const onSubmit = (e) => {
    e.preventDefault();
    const updateEmployee = {
      id: state.id,
      name: state.name,
      address: state.address,
      email: state.email,
      phone: state.phone,
    };
    dispatch(createEmployee(updateEmployee, dep_id));
  };

  return (
    <div className="bg-gradient-to-b from-blue-400 to-blue-600 min-h-screen flex items-center justify-center">
    <div className="bg-white p-8 rounded-lg shadow-lg w-full sm:w-2/3 lg:w-1/2">
      <h1 className="text-3xl font-semibold text-gray-800 text-center mb-4">Update Employee Form</h1>
      <hr className="border-gray-300 mb-4" />
  
      <form onSubmit={onSubmit}>
        <div className="mb-4">
          <input
            type="text"
            className={`w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600 ${
              errors.name ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="ID"
            name="id"
            disabled
            value={state.id}
            onChange={onChange}
          />
        </div>
  
        <div className="mb-4">
          <input
            type="text"
            className={`w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600 ${
              errors.name ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Employee Name"
            value={state.name}
            onChange={onChange}
            name="name"
          />
          {errors.name && <div className="text-red-500 mt-2">{errors.name}</div>}
        </div>
  
        <div className="mb-4">
          <input
            type="text"
            className={`w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600 ${
              errors.address ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Employee Address"
            name="address"
            value={state.address}
            onChange={onChange}
          />
          {errors.address && <div className="text-red-500 mt-2">{errors.address}</div>}
        </div>
  
        <div className="mb-4">
          <input
            type="text"
            className={`w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600 ${
              errors.email ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Employee Email"
            name="email"
            value={state.email}
            onChange={onChange}
          />
          {errors.email && <div className="text-red-500 mt-2">{errors.email}</div>}
        </div>
  
        <div className="mb-6">
          <input
            type="text"
            className={`w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600 ${
              errors.phone ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Phone Number"
            name="phone"
            value={state.phone}
            onChange={onChange}
          />
          {errors.phone && <div className="text-red-500 mt-2">{errors.phone}</div>}
        </div>
  
        <button className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 px-6 rounded-lg w-full">
          Update Employee
        </button>
      </form>
    </div>
  </div>
  );
};

export default UpdateEmployee;
