import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import axios from 'axios';

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare the data to send to the server
    const userData = {
      name,
      email,
      password,
    };

    try {
      // Make an API POST request to register the user
      const response = await axios.post('/register', userData);

      // Handle the response, e.g., show a success message to the user
      console.log(response.data);

      // Redirect to a different page after successful registration
      history.push('/login'); // Redirect to the login page
    } catch (error) {
      // Handle registration errors, e.g., show an error message
      console.error(error);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-500 via-green-400 to-indigo-700">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md text-center">
        <h2 className="text-3xl font-extrabold text-gray-800 mb-6">Create a new account</h2>
        <form className="space-y-6" onSubmit={handleSubmit}>
          {/* ...your form inputs */}
          <div>
            <button
              type="submit"
              className="w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              Register
            </button>
          </div>
        </form>
        <div className="text-gray-700 text-sm mt-4">
          Already have an account? <Link to="/login" className="text-indigo-600">Log in here</Link>
        </div>
      </div>
    </div>
  );
};

export default Register;
