import axios from "axios";
import {
  GET_TASKS,
  GET_ERRORS,
  CREATE_TASK,
  GET_TASK,
  DELETE_TASK,
} from "./types";

export const getTasks = (dep_id, emp_id) => async (dispatch) => {
  const res = await axios.get(
    `http://localhost:8095/api/tasks/${dep_id}/${emp_id}`
  );
  dispatch({
    type: GET_TASKS,
    payload: res.data,
  });
};

export const createTask = (task, dep_id, emp_id) => async (dispatch) => {
  try {
    await axios.post(
      `http://localhost:8095/api/tasks/${dep_id}/${emp_id}`,
      task
    );
    window.location.href = `/employeeBoard/${dep_id}/${emp_id}`;
    // You should not dispatch CREATE_TASK action here because it doesn't return data from the server.
  } catch (error) {
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data,
    });
  }
};

export const getTask = (dep_id, emp_id, id) => async (dispatch) => {
  try {
    const res = await axios.get(`http://localhost:8095/api/tasks/${dep_id}/${emp_id}/${id}`);
    dispatch({
      type: GET_TASK,
      payload: res.data,
    });
  } catch (error) {
    console.log(error);
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data,
    });
  }
};


export const deleteTask = (dep_id, emp_id, id) => async (dispatch) => {
  if (window.confirm("Are you sure you want to delete this Task?")) {
    await axios.delete(`http://localhost:8095/api/tasks/${dep_id}/${emp_id}/${id}`);
    dispatch({
      type: DELETE_TASK,
      payload: id,
    });
  }
};


export const updateTask = (updatedTask, dep_id, emp_id, id) => async (dispatch) => {
  try {
    const res = await axios.put(
      `http://localhost:8095/api/tasks/${dep_id}/${emp_id}/${id}`,
      updatedTask
    );

    // Dispatch an action to handle the successful update
    dispatch({
      type: GET_TASKS, // Use the appropriate action type
      payload: res.data, // You may update the payload based on your needs
    });
  } catch (error) {
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data,
    });
  }
};
