import React from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { deleteDepartment } from "../../actions/DepartmentActions";

function Department(props) {
  const dispatch = useDispatch();
  const { department } = props;

  if (!department) {
    // Handle the case where department is undefined
    return <div>Department not found</div>;
  }

  const onDeleteClick = (id) => {
    dispatch(deleteDepartment(id));
  };

  return (
    <div className="bg-white p-3 mb-3 rounded-md">
    <div className="container">
      <h2 className="text-2xl font-semibold">Departments</h2>
      <div className="p-3 bg-white shadow-md rounded-md">
        <div className="flex justify-between items-center">
        <div className="w-9/12 ml-20">
  <h3 className="text-xl font-semibold text-center">
    {department.name}
  </h3>
</div>
<div className="w-3/12 text-right">
  <ul className="space-y-4">
    <li>
      <Link
        to={`/employees/${department.id}`}
        className="text-white hover:text-white focus:outline-none bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-full transition duration-300 ease-in-out transform hover:scale-105 block w-full text-center"
      >
        Department Board
      </Link>
    </li>
    <li>
      <Link
        to={`/updateDepartment/${department.id}`}
        className="text-white hover:text-white focus:outline-none bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-full transition duration-300 ease-in-out transform hover:scale-105 block w-full text-center"
      >
        Update Department
      </Link>
    </li>
    <li>
      <button
        className="text-white hover:text-white focus:outline-none bg-red-500 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded-full transition duration-300 ease-in-out transform hover:scale-105 block w-full text-center"
        onClick={() => onDeleteClick(department.id)}
      >
        Delete Department
      </button>
    </li>
  </ul>
</div>


        </div>
      </div>
    </div>
  </div>
  );
}

Department.propTypes = {
  department: PropTypes.object,
};

export default Department;
