import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import { updateTask, getTask } from "../../actions/TaskActions";

const UpdateTask = () => {
  const { dep_id, emp_id, id } = useParams();
  const dispatch = useDispatch();
  const errors = useSelector((state) => state.errorsReducerContent);
  const task = useSelector((state) => state.taskReducerContent.task);
  
  const [state, setState] = useState({
    summary: "",
    acceptanceCriteria: "",
    status: "INPUT QUEUE",
    priority: 3,
    errors: {},
  });

  useEffect(() => {
    dispatch(getTask(dep_id, emp_id, id));
  }, [dispatch, dep_id, emp_id, id]);

  useEffect(() => {
    setState((prevState) => ({
      ...prevState,
      id: id,
      summary: task.summary || "",
      acceptanceCriteria: task.acceptanceCriteria || "",
      priority: task.priority || "",
      status: task.status || "",
      errors: errors,
    }));
  }, [task, id, errors]);

  const onChange = (e) => {
    setState((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const onSubmit = (e) => {
    e.preventDefault();
    
    // Perform basic validation
    const errors = {};
    if (!state.summary.trim()) {
      errors.summary = 'Summary is required';
    }
    if (!state.acceptanceCriteria.trim()) {
      errors.acceptanceCriteria = 'Acceptance Criteria is required';
    }
    if (state.priority === 0) {
      errors.priority = 'Priority is required';
    }
    if (!state.status) {
      errors.status = 'Status is required';
    }
  
    if (Object.keys(errors).length === 0) {
      const updatedTask = {
        summary: state.summary,
        status: state.status,
        acceptanceCriteria: state.acceptanceCriteria,
        priority: state.priority,
      };
      dispatch(updateTask(updatedTask, dep_id, emp_id, id));
    } else {
      // Set the errors in the component state
      setState((prevState) => ({
        ...prevState,
        errors,
      }));
    }
  };
  

  return (
    <div className="container">
      <div className="add-PBI">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <Link
                to={`/employeeBoard/${dep_id}/${emp_id}`}
                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg"
              >
                Back to Employee Board
              </Link>
              <h4 className="text-2xl text-center mt-6">Update Task</h4>
              <form onSubmit={onSubmit} className="space-y-6">
                <div className="form-group">
                  <input
                    type="text"
                    className={`w-full p-3 border border-gray-300 rounded-md ${
                      errors.summary ? "border-red-500" : ""
                    }`}
                    name="summary"
                    placeholder="Project Task summary"
                    value={state.summary}
                    onChange={onChange}
                  />
                  {errors.summary && (
                    <p className="text-red-500 text-xs mt-1">{errors.summary}</p>
                  )}
                </div>
                <div className="form-group">
                  <textarea
                    className={`w-full p-3 border border-gray-300 rounded-md ${
                      errors.acceptanceCriteria ? "border-red-500" : ""
                    }`}
                    placeholder="Acceptance Criteria"
                    name="acceptanceCriteria"
                    value={state.acceptanceCriteria}
                    onChange={onChange}
                  ></textarea>
                  {errors.acceptanceCriteria && (
                    <p className="text-red-500 text-xs mt-1">{errors.acceptanceCriteria}</p>
                  )}
                </div>
                <div className="form-group">
                  <select
                    className={`w-full p-3 border border-gray-300 rounded-md ${
                      errors.priority ? "border-red-500" : ""
                    }`}
                    name="priority"
                    value={state.priority}
                    onChange={onChange}
                  >
                    <option value={0}>Select Priority</option>
                    <option value={1}>High</option>
                    <option value={2}>Medium</option>
                    <option value={3}>Low</option>
                  </select>
                  {errors.priority && (
                    <p className="text-red-500 text-xs mt-1">{errors.priority}</p>
                  )}
                </div>
                <div className="form-group">
                  <select
                    className={`w-full p-3 border border-gray-300 rounded-md ${
                      errors.status ? "border-red-500" : ""
                    }`}
                    name="status"
                    value={state.status}
                    onChange={onChange}
                  >
                    <option value="">Select Status</option>
                    <option value="INPUT QUEUE">INPUT QUEUE</option>
                    <option value="IN_PROGRESS">IN PROGRESS</option>
                    <option value="DONE">DONE</option>
                  </select>
                  {errors.status && (
                    <p className="text-red-500 text-xs mt-1">{errors.status}</p>
                  )}
                </div>
                <div className="form-group">
                <input
  type="submit"
  className="w-full bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-md"
/>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateTask;
