import React, { useEffect } from "react";
import Department from "./departments/Department";
import CreateButton from "./departments/CreateButton";
import { getDepartments } from "../actions/DepartmentActions";
import { useSelector, useDispatch } from "react-redux";

function Departments() {
  const dispatch = useDispatch();
  const departmentList = useSelector(
    (state) => state.departmentReducerContent.departments
  );

  useEffect(() => {
    dispatch(getDepartments());
  }, [dispatch]);

  return (
    <div className="container mx-auto">
      <CreateButton />

      <div className="grid grid-cols-1 gap-4 md:grid-cols-2 lg:grid-cols-3">
        {departmentList.map((department) => (
          <div
            key={department.id}
            className="bg-white rounded-lg shadow-md p-4"
          >
            <h3 className="text-xl font-semibold">{department.name}</h3>
            <ul className="mt-2">
              <li>
                <a
                  href={`/employees/${department.id}`}
                  className="text-blue-500 hover:underline"
                >
                  Employee Board
                </a>
              </li>
              <li>
                <a
                  href={`/updateDepartment/${department.id}`}
                  className="text-blue-500 hover:underline"
                >
                  Update Department
                </a>
              </li>
              <li>
                <button
                  onClick={() => onDeleteClick(department.id)}
                  className="text-red-500 hover:underline"
                >
                  Delete Department
                </button>
              </li>
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Departments;
