import React from "react";
import { Link } from "react-router-dom";

const CreateButton = () => {
  return (
    <div className="my-4 text-center">
      <Link
        to="/addDepartment"
        className="bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-full shadow-md transition duration-300 transform hover:scale-105 inline-block"
      >
        Create a department
      </Link>
    </div>
  );
};

export default CreateButton;
