// EmployeeManagementPage.js

import React from 'react';
import { Disclosure } from '@headlessui/react';

const EmployeeManagementPage = () => {
  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-b from-blue-500 to-indigo-700 text-white">
      {/* Header */}
      <header className="bg-gray-800 py-4">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-3xl font-extrabold">Employee Management</h1>
          <button className="bg-blue-500 hover:bg-blue-600 px-4 py-2 rounded-md">
            Log Out
          </button>
        </div>
      </header>

      {/* Hero Section */}
      <section className="flex-1 container mx-auto flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-5xl font-extrabold mb-4">Welcome to Your Dashboard</h2>
          <p className="text-lg mb-8">
            Manage your employees efficiently and effortlessly.
          </p>
          <button className="bg-indigo-500 hover:bg-indigo-600 text-white px-6 py-3 rounded-full font-semibold transition duration-300">
            Get Started
          </button>
        </div>
      </section>

      {/* Features Section */}
      <section className="bg-gray-100 py-16">
        <div className="container mx-auto text-center">
          <h2 className="text-4xl font-extrabold mb-12">Key Features</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {/* Feature 1 */}
            <FeatureCard title="Employee Directory" description="Easily manage and organize your employee data in one place." />

            {/* Feature 2 */}
            <FeatureCard title="Task Management" description="Assign and track tasks efficiently for better productivity." />

            {/* Feature 3 */}
            <FeatureCard title="Reports & Analytics" description="Gain insights into your workforce with comprehensive analytics." />
          </div>
        </div>
      </section>
    </div>
  );
};

const FeatureCard = ({ title, description }) => (
  <div className="bg-white p-8 rounded-md shadow-md transition duration-300 transform hover:scale-105">
    <h3 className="text-xl font-semibold mb-4">{title}</h3>
    <p className="text-gray-600">{description}</p>
  </div>
);

export default EmployeeManagementPage;
