import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { Provider } from "react-redux";
import store from "./store";
import Header from "./components/Layout/Header";
import Departments from "./components/Departments";
import AddDepartment from "./components/departments/AddDepartment";
import UpdateDepartment from "./components/departments/UpdateDepartment";
import DepartmentBoard from "./components/DepartmentBoard";
import AddEmployee from "./components/employees/AddEmployees";
import UpdateEmployee from "./components/employees/UpdateEmployees";
import EmployeeBoard from "./components/EmployeeBoard";
import AddTaskForm from "./components/tasks/AddTaskForm";
import UpdateTask from "./components/tasks/UpdateTask";
import Landing from "./components/Landing";
import Login from "./components/LogIn";
import Register from "./components/Register";

function checkAuthentication() {
  // Replace this logic with your actual authentication check
  // For example, check if a user token exists in local storage.
  return localStorage.getItem('userToken') !== null;
}

function App() {
  const isAuthenticated = checkAuthentication();
  console.log('Is authenticated:', isAuthenticated);

  return (
    <Provider store={store}>
      <Router>
        <div>
          {/* Render the Header for all routes except Landing, Login, and Register */}
          <Header />

          <Routes>
            <Route path="/" element={<Landing />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />

            {/* Protected Routes */}
            {isAuthenticated ? (
              <>
                <Route path="/departments" element={<Departments />} />
                <Route path="/addDepartment" element={<AddDepartment />} />
                <Route path="/updateDepartment/:id" element={<UpdateDepartment />} />
                <Route path="/employees/:id" element={<DepartmentBoard />} />
                <Route path="/addEmployee/:id" element={<AddEmployee />} />
                <Route path="/updateEmployee/:dep_id/:id" element={<UpdateEmployee />} />
                <Route path="/employeeBoard/:dep_id/:emp_id" element={<EmployeeBoard />} />
                <Route path="/addTask/:dep_id/:emp_id" element={<AddTaskForm />} />
                <Route path="/updateTask/:dep_id/:emp_id/:id" element={<UpdateTask />} />
              </>
            ) : (
              // Redirect to login if not authenticated
              <Route path="*" element={<Navigate to="/login" />} />
            )}
          </Routes>
        </div>
      </Router>
    </Provider>
  );
}

export default App;
