import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { Provider } from "react-redux";
import store from "./store";
import Header from "./components/Layout/Header";
import Departments from "./components/Departments";
import AddDepartment from "./components/departments/AddDepartment";
import UpdateDepartment from "./components/departments/UpdateDepartment";
import DepartmentBoard from "./components/DepartmentBoard";
import AddEmployee from "./components/employees/AddEmployees";
import UpdateEmployee from "./components/employees/UpdateEmployees";
import EmployeeBoard from "./components/EmployeeBoard";
import AddTaskForm from "./components/tasks/AddTaskForm";
import UpdateTask from "./components/tasks/UpdateTask";
import Landing from "./components/Landing";
import Login from "./components/LogIn";
import Register from "./components/Register";

// Create a PrivateRoute component for protecting routes
function PrivateRoute({ element, isAuthenticated }) {
  return isAuthenticated ? element : <Navigate to="/login" />;
}

function checkAuthentication() {
  return localStorage.getItem('userLoggedIn') === 'true';
}

function App() {
  const isAuthenticated = checkAuthentication();

  return (
    <Provider store={store}>
      <Router>
        <div>
          {/* Render the Header for all routes except Landing, Login, and Register */}
          <Header />

          <Routes>
            <Route path="/" element={<Landing />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />

            {/* Protected Routes using PrivateRoute */}
            <PrivateRoute path="/departments" element={<Departments />} isAuthenticated={isAuthenticated} />
            <PrivateRoute path="/addDepartment" element={<AddDepartment />} isAuthenticated={isAuthenticated} />
            <PrivateRoute path="/updateDepartment/:id" element={<UpdateDepartment />} isAuthenticated={isAuthenticated} />
            {/* Add other protected routes similarly */}
          </Routes>
        </div>
      </Router>
    </Provider>
  );
}

export default App;
