import React from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { deleteTask } from "../../actions/TaskActions";

function Task(props) {
  const { dep_id } = props;
  const { emp_id } = props;
  const { task } = props;
  const dispatch = useDispatch();

  const onDeleteClick = (id, dep_id, emp_id) => {
    dispatch(deleteTask(dep_id, emp_id, id));
  };


  let priorityClassName;
  let priorityString;

  if (task.priority === 1) {
    priorityClassName = "bg-red-500 text-white";
    priorityString = "HIGH";
  } else if (task.priority === 2) {
    priorityClassName = "bg-yellow-500 text-black";
    priorityString = "MEDIUM";
  } else if (task.priority === 3) {
    priorityClassName = "bg-blue-500 text-white";
    priorityString = "LOW";
  }

  return (
    <div className="bg-light mb-4 p-4 rounded-lg">
      <div className={`text-primary ${priorityClassName} p-4 rounded-t-lg`}>
        <h3 className="text-lg font-semibold">
          ID: {task.id} -- Priority: {priorityString}
        </h3>
      </div>
      <div className="bg-light p-4 rounded-b-lg">
        <h5 className="text-xl font-semibold">{task.summary}</h5>
        <p className="text-gray-700 text-truncate">{task.acceptanceCriteria}</p>
        <div className="flex mt-4">
          <Link
            to={`/updateTask/${dep_id}/${emp_id}/${task.id}`}
            className="bg-primary text-white px-4 py-2 rounded-lg mr-2 hover:bg-primary-dark"
          >
            View / Update
          </Link>
          <button
  className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600"
  onClick={() => onDeleteClick(task.id, dep_id, emp_id)}
>
  Delete
</button>
        </div>
      </div>
    </div>
  );
}

export default Task;
