import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getTasks } from "../actions/TaskActions";
import { Link, useParams } from "react-router-dom";
import Task from "../components/tasks/Task";

const EmployeeBoard = () => {
  const { dep_id, emp_id } = useParams();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getTasks(dep_id, emp_id));
  }, [dep_id, emp_id, dispatch]);

  const taskList = useSelector((state) => state.taskReducerContent.tasks);

  let inputQueue = [];
  let inProgress = [];
  let done = [];

  for (const task of taskList) {
    if (task.status === "INPUT QUEUE") {
      inputQueue.push(task);
    } else if (task.status === "IN_PROGRESS") {
      inProgress.push(task);
    } else if (task.status === "DONE") {
      done.push(task);
    }
  }

  return (
    <div className="container mx-auto p-4">
      <Link
        to={`/addTask/${dep_id}/${emp_id}`}
        className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded-md text-lg inline-block mb-4"
      >
        <i className="fas fa-plus-circle mr-2"></i> Create Employee Task
      </Link>
      <hr />
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
        <div className="bg-secondary text-white rounded-lg">
          <div className="text-2xl font-bold p-3 bg-secondary-darker rounded-t-lg">
            Input Queue
          </div>
          <div className="p-4">
            {inputQueue.map((task) => (
              <Task key={task.id} dep_id={dep_id} emp_id={emp_id} task={task} />
            ))}
          </div>
        </div>
        <div className="bg-primary text-white rounded-lg">
          <div className="text-2xl font-bold p-3 bg-primary-darker rounded-t-lg">
            In Progress
          </div>
          <div className="p-4">
            {inProgress.map((task) => (
              <Task key={task.id} dep_id={dep_id} emp_id={emp_id} task={task} />
            ))}
          </div>
        </div>
        <div className="bg-success text-white rounded-lg">
          <div className="text-2xl font-bold p-3 bg-success-darker rounded-t-lg">
            Done
          </div>
          <div className="p-4">
            {done.map((task) => (
              <Task key={task.id} dep_id={dep_id} emp_id={emp_id} task={task} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmployeeBoard;
