import React from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { deleteTask } from "../../actions/TaskActions";

function Task(props) {
  const { dep_id } = props;
  const { emp_id } = props;
  const { task } = props;
  const dispatch = useDispatch();

  const onDeleteClick = (id, dep_id, emp_id) => {
    dispatch(deleteTask(dep_id, emp_id, id));
  };


  let priorityClassName;
  let priorityString;

  if (task.priority === 1) {
    priorityClassName = "bg-red-500 text-black";
    priorityString = "HIGH";
  } else if (task.priority === 2) {
    priorityClassName = "bg-yellow-500 text-black";
    priorityString = "MEDIUM";
  } else if (task.priority === 3) {
    priorityClassName = "bg-blue-500 text-black";
    priorityString = "LOW";
  }

  return (

    <div className="container bg-white p-4 rounded-lg mb-4 shadow-md flex items-center justify-between">
      <div className="flex-1">
        <h3 className="text-xl font-semibold mb-2">{employee.name}</h3>
        <p className="text-gray-600">{employee.address}</p>
        <p className="text-gray-600">{employee.email}</p>
        <p className="text-gray-600">{employee.phone}</p>
      </div>
      <div className="lg:block hidden">
        <ul className="space-y-4">
          <li>
            <Link
              to={`/updateTask/${dep_id}/${emp_id}/${task.id}`}
              className="text-white hover:text-white focus:outline-none bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-full transition duration-300 ease-in-out transform hover:scale-105 block w-full text-center"
            >
              View / Update
            </Link>
          </li>
          <li>
            <button
              className="text-white hover:text-white focus:outline-none bg-red-500 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded-full transition duration-300 ease-in-out transform hover:scale-105 block w-full text-center"
              onClick={() => onDeleteClick(task.id, dep_id, emp_id)}
            >
              Delete
            </button>
          </li>
        </ul>
      </div>
    </div>
  
  
  );
}

export default Task;
