import React from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { deleteDepartment } from "../../actions/DepartmentActions";

function Department(props) {
  const dispatch = useDispatch();
  const { department } = props;

  if (!department) {
    // Handle the case where department is undefined
    return <div>Department not found</div>;
  }

  const onDeleteClick = (id) => {
    dispatch(deleteDepartment(id));
  };

  return (
    <div className="bg-gray-100 p-3 mb-3 rounded-md">
      <div className="container">
        <h2 className="text-2xl font-semibold">Departments</h2>
        <div className="p-3 bg-white shadow-md rounded-md">
          <div className="flex">
            <div className="w-9/12">
              <h3 className="text-lg font-semibold">{department.id}</h3>
              <h3 className="text-lg font-semibold">{department.name}</h3>
            </div>
            <div className="w-3/12">
              <ul>
                <Link to={`/employees/${department.id}`}>
                  <li className="cursor-pointer text-blue-500 hover:underline">
                    <i className="fas fa-edit pr-1"></i> Department board
                  </li>
                </Link>
                <Link to={`/updateDepartment/${department.id}`}>
                  <li className="cursor-pointer text-blue-500 hover:underline">
                    <i className="fas fa-edit pr-1"></i> Update department
                  </li>
                </Link>
                <Link to="" onClick={() => onDeleteClick(department.id)}>
                  <li className="cursor-pointer text-red-500 hover:underline">
                    <i className="fas fa-minus-circle pr-1"></i> Delete department
                  </li>
                </Link>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

Department.propTypes = {
  department: PropTypes.object,
};

export default Department;
